INSERT INTO `dedicatedpages` (`IdPg`, `NewPage`, `Folder`, `Title`, `Subtitle`, `Text`, `TmbLink`, `ImgLink`, `LinkRifPage`) VALUES
(32, 'no', '', 'La notte dei calcolatori raccontati', 'Sabato 18 maggio, dalle 20.30, La Notte dei Musei al Museo degli Strumenti per il Calcolo', '<p>L\'informatica &egrave; una disciplina scientifica e tecnologica,<br />ma spesso &egrave; presentata in modi che sfuggono al rigore<br />e alla concretezza del suo naturale contesto.<br />Nella Notte dei Musei i calcolatori sono raccontati<br />attraverso la&nbsp;<strong>pubblicit&agrave;</strong>&nbsp;e l\'<strong>immaginario</strong>.<br />Citazioni, foto e filmati sono commentati dal vivo<br />per ragionare sulle&nbsp;<strong>lusinghe</strong>&nbsp;dell\'informatica di consumo<br />e per rivivere le&nbsp;<strong>suggestioni</strong>&nbsp;di chi, immaginando il futuro<br />delle macchine, ha visto prestazioni strabilianti<br />e persino intelligenza e sentimenti da sempre considerati<br />prerogativa dell\'uomo.<br />Come da tradizione, la lettura proposta da HMR<br />non tralascia gli aspetti tecnologici svelando dettagli curiosi,<br />seducenti inganni ed eccessivi ottimismi.</p>\r\n<h3>Programma</h3>\r\n<strong>20.30</strong>&nbsp;Apertura del museo<br /><strong>21.00-21.45</strong>&nbsp;<a href=\"../Riferimenti.html#2013i-CalcRacc\">Il calcolatore percepito</a>, mercato e pubblicit&agrave;<br /><strong>22.30-23.15</strong>&nbsp;<a href=\"../Riferimenti.html#2013i-CalcRacc\">Il calcolatore immaginato</a>, letteratura e cinema\r\n<p>&nbsp;Fra un racconto e l\'altro e dopo, fino a mezzanotte circa,<br />le sale del Museo sono visitabili liberamente.<br />L\'ingresso &egrave; gratuito.<br />La Notte dei Calcolatori &egrave;&nbsp;<a href=\"http://www.lanottedeimusei.it/immagini\" target=\"_blank\" rel=\"noopener\">Instagramers compatibile</a>.<br />Siamo pi&ugrave; che contenti che la serata sia condivisa in rete :)</p>\r\n<p>Le altre Notti dei Musei firmate HMR:&nbsp;<a href=\"../NVV2014/index.html\">2014</a>,&nbsp;<a href=\"../NotteMusei2015/index.html\">2015</a></p>', 'HMR_NotteMusei2013_t.jpg', '', 'http://www.progettohmr.it/NotteMusei2013/'),
(42, 'no', '', 'Divulgazione, didattica e studio dell\'informatica', 'Giornata di studi per l\'inaugurazione della Sala Dall\'aritmometro al PC, venerd√¨ 20 dicembre 2013, h 14.00, Museo degli Strumenti per il Calcolo', '<p>Il Museo degli Strumenti per il Calcolo dell\'Universit&agrave; di Pisa conserva una collezione sull\'informatica personale<br />unica in Italia e invidiabile in Europa.<br />Un tesoro che, a causa della riduzione degli spazi disponibili per il Museo, da diversi anni non era visibile al pubblico.<br />Una prima uscita, limitata nei tempi e negli spazi, era stata organizzata in occasione dell\'Internet Festival 2012.</p>\r\n<p>A distanza di poco pi&ugrave; di un anno, l\'informatica personale torna a essere protagonista degli allestimenti permanenti del Museo con un percorso arricchito da quell\'esperienza e da molti mesi di ricerche e di lavoro sui pezzi.</p>\r\n<p>La nuova Sala del Museo, curata da Giovanni A. Cignoni, presenta un viaggio che, idea dopo idea, ci porta<br />dagli aritmometri dell\'800 ai PC portatili, toccando eccellenze<br />della nostra storia tecnologica, per tutte la Programma 101,<br />e protagonisti di passioni collettive - i personal e<br />gli home computer degli anni \'80.</p>\r\n<p>La Sala si inserisce in un pi&ugrave; ampio progetto di rinnovamento di un Museo - Universitario - che vuole essere centro attivo per la diffusione, la didattica e lo studio dell\'informatica.</p>\r\n<p>La giornata inaugurale &egrave; dedicata proprio a questi temi e, come buon auspicio per la funzione didattica del Museo, in collaborazione con AICA, nel programma della giornata &egrave; inserita la premiazione di quattro giovani promesse per l\'informatica di domani.</p>\r\n<p>&nbsp;</p>', 'HMR_20131220-InaugAritPC_t.jpg', 'HMR_20131220-InaugAritPC.jpg', 'http://www.progettohmr.it/AritPC'),
(43, 'no', '', '', '', '', '', '', 'http://www.progettohmr.it/PaginaQ/'),
(49, 'no', '', 'La Luna da Galileo all\'Apollo 11', 'Gioved√¨ 12 giugno 2014, dalle 20.30, serata al Museo degli Strumenti per il Calcolo', '<p>La Luna &egrave; sempre stata un obiettivo raggiungibile nell\'immaginario: da Icaromenippo a Barbicane &amp; C., passando per Astolfo, Cyrano, il Barone di Munchausen<br />e tanti altri eroi della fantasia.<br />Con la scienza il sogno diventa impresa possibile.<br />Dalle osservazioni di&nbsp;<strong>Galileo</strong>&nbsp;alla meccanica celeste<br />di&nbsp;<strong>Newton</strong>&nbsp;cadono i veli del mistero, ma fra noi e la Luna<br />c\'&egrave; di mezzo un mare di calcoli da affrontare,<br />dalla&nbsp;<strong>velocit&agrave; di fuga</strong>&nbsp;all\'equazione di&nbsp;<strong>Tsiolkovsky</strong>.<br />Rigorosa, come nello stile HMR, ma senza insistere troppo<br />nei dettagli, e con l\'intento di incuriosire e intrattenere intelligentemente grandi e piccini, la serata incrocia i riferimenti ai classici con i meccanismi dei voli spaziali<br />e racconta i calcolatori che, risolvendo i calcoli, permisero l\'impresa ma anche riservarono qualche sorpresa, come negli ultimi concitati minuti dell\'allunaggio di&nbsp;<strong>Apollo 11</strong>.<br />Per i pi&ugrave; piccoli un laboratorio curato da&nbsp;<em>Correlamente</em>:<br />Galileo con il cannocchiale osserv&ograve;, studi&ograve; e disegn&ograve; i crateri lunari, nella serata li ricreeremo giocando con la cartapesta.<br />La serata &egrave; speciale anche per il cielo che ci regala.<br />Proveremo a ripetere le esperienze di Galileo osservando<br />la Luna (piena), Marte e Saturno, che dalle 23 saranno<br /><a href=\"Sky_20140612-180-23.png\" target=\"_blank\" rel=\"noopener\">visibili</a>&nbsp;a Sud (nuvole e luci della citt&agrave; permettendo).<br />Infine, per brindare alla Luna, avremo anche di che bere e mangiare grazie a&nbsp;<em>The Thing</em>. Per il Museo una novit&agrave; assoluta e da tanto tempo attesa :)</p>', 'MSC_20140612Luna_t.jpg', 'MSC_20140612Luna.jpg', 'http://www.progettohmr.it/Luna/'),
(61, 'no', '', '', '', '', '', '', 'http://www.progettohmr.it/LezioniAlMuseo/'),
(66, 'no', '', 'Laboratorio di videogiochi', 'dal 13.11.2014 al 25.06.2015, tutti i gioved√¨ 18.00-19.30 Sala Laboratori HMR, <a href=\'http://www.fondazionegalileogalilei.it/\'>Museo degli Strumenti per il Calcolo</a>', '<p>Negli ultimi anni si sono moltiplicati sia i programmi per realizzare videogiochi senza possedere particolari competenze di programmazione, sia le opportunit&agrave; distributive concesse a sviluppatori indipendenti e non, grazie agli store online e alle piattaforme mobili.<br />Tuttavia, se oggi ciascuno di noi pu&ograve; avere una piccola sala giochi in tasca, la risposta alla domanda \"cosa &egrave; che fa un buon videogioco?\" non &egrave; cambiata. La risposta si trova negli esempi correnti e nella storia del mezzo espressivo, ma soprattutto nelle capacit&agrave; di chi nel mezzo si cimenta.<br />Il laboratorio nasce per identificare e sviluppare queste competenze nei partecipanti, e metterle in comune.<br />La partecipazione &egrave; aperta a tutti coloro che abbiano competenze informatiche di base ed &egrave; gratuita. Partecipanti versati in uno o pi&ugrave; aspetti della realizzazione di videogiochi sono benvenuti per l\'approfondimento e il lavoro di gruppo.</p>\r\n<h3><strong>Play!</strong></h3>\r\n<p>Marted&igrave; 30 Maggio, dalle 21, al Museo</p>\r\n<p>Conclusione del corso e presentazione al pubblico<br />dei videogiochi realizzati:</p>\r\n<ul>\r\n<li><a href=\"Gioco1/index.html\">R_Evolution</a></li>\r\n<li><a href=\"Gioco2/index.html\">Boys don\'t Cray</a></li>\r\n<li><a href=\"Gioco3/index.html\">Nikola e i fantasmi del passato</a></li>\r\n</ul>\r\n<p>Venite, giocate e diteci quale gioco<br />rappresenta meglio il Museo!</p>\r\n<p>&nbsp;</p>', 'LabVG-Locandina.png', '', 'http://www.progettohmr.it/LabVG/'),
(68, 'no', '', 'Games, from Engaging to Understanding', 'a perspective from a Museum of Computing Machinery,  poster & short paper at <a href=\'http://icec2015.idi.ntnu.no/\'>IFIP-ICEC 2015</a>', '<p>The paper presents the HMR experiences<br />in the adoption of several kinds of gaming approaches<br />for attracting and involving the public of the Museum<br />of Computing Machinery of the University of Pisa.</p>\r\n<p><strong>The Nights of Old Videogames</strong>&nbsp;a retrogaming tournament<br />already played for four editions:</p>\r\n<ul>\r\n<li><a href=\"../NVV2014/index.html\">2014</a>, Prepare to be invaded;</li>\r\n<li><a href=\"../NVV2014if/index.html\">2014, special IF</a>, Since \'81 and still jumping;</li>\r\n<li><a href=\"../NVV2015/index.html\">2015</a>, Don\'t be shy;</li>\r\n<li><a href=\"../NVV2015airo/index.html\">2015, special AIRO</a>, Killing Polyhedral Models.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><strong>Challenges on Old Machines</strong>&nbsp;visitors and students are<br />challenged to operate old machines, like an old&nbsp;<a href=\"../Documenti/HMR_2014d_GCGL-Manovellando.pdf\">Brunsviga</a>,<br />or the&nbsp;<a href=\"../Ricostruzioni.html\">Macchina Ridotta</a>, the very first italian computer<br />virtually rebuilded as an accurate real-time simulator.</p>\r\n<p><strong>Ad hoc Edugames</strong>&nbsp;as the&nbsp;<a href=\"../Blob/index.html\">Blob Game of Life</a>, a simulation<br />of a closed ecosystem ispired by historical mathematical<br />models like the Lotka-Volterra one.</p>\r\n<p><strong>Video Game Lab and toy-games</strong>, a&nbsp;<a href=\"../LabVG/index.html\">course</a>&nbsp;in video game<br />making, held from November 2014 to June 2015, saw<br />the development of three simple games centered around<br />the Museum and its collection:</p>\r\n<ul>\r\n<li><a href=\"../LabVG/Gioco1/index.html\">R_Evolution</a></li>\r\n<li><a href=\"../LabVG/Gioco2/index.html\">Boys don\'t Cray</a></li>\r\n<li><a href=\"../LabVG/Gioco3/index.html\">Nikola and the Ghosts of the Past</a></li>\r\n</ul>\r\n<p>&nbsp;The&nbsp;<a href=\"../Riferimenti.html#2015s-ICEC\">HMR paper</a>&nbsp;at IFIP-ICEC.</p>\r\n<p>IFIP-ICEC is the International Conference on Entertainment Computing. It is the longest established and most prestigious conference<br />in the field of entertainment computing. This is the 14th edition.</p>\r\n<p>An&nbsp;<a href=\"../Riferimenti.html#2015i-CultDig\">extended presentation</a>&nbsp;(in Italian) of the ICEC paper has been held in Pisa, October 7 as one of the \"Seminari di cultura digitale\" organized by&nbsp;<em>Informatica Umanistica</em>, in Pisa.</p>', 'HMR_2015s_GCLCTM-ICECposter_t.jpg', 'HMR_2015s_GCLCTM-ICECposter.jpg', 'http://www.progettohmr.it/ICEC2015/'),
(82, 'no', '', 'Il Dandy, il Porco, il Cattivo', 'una serata di chiacchiere e videogiochi, 29 aprile 2016, h 19, al <a href=\'https://www.facebook.com/CaffeLetterarioVoltaPagina\'>Caffe Letterario Voltapagina</a>, Via S. Martino, Pisa', '<p>Videogiochi e tecnologia, videogiochi e arte, videogiochi<br />e tendenze, videogiochi e satira, videogiochi e basta!<br />Una serata di chiacchiere in tema al Caffe Letterario Voltapagina con la partecipazione di:</p>\r\n<ul>\r\n<li><strong>Marco \'Alfieri\' Guzzo</strong>,<br />autore di&nbsp;<em>Call Of Salveenee</em>&nbsp;e&nbsp;<em>D\'Annunzio Cola</em>,</li>\r\n<li><strong>Marco Lorenzini</strong>&nbsp;e&nbsp;<strong>Paolo \'Pollo\' Cioni</strong>,<br />il duo di&nbsp;<em>HungryPixel\'s Ludiqa</em>, reduci dall\'uscita<br />di&nbsp;<em>Undead Nerds</em>&nbsp;e&nbsp;<em>Richard Part One</em>,</li>\r\n<li><strong>Tommaso Mongelli</strong>, autore di giochi con&nbsp;<em>Quikding LLC</em><br />e prossimamente&nbsp;<em>SEIMI Games</em>,</li>\r\n</ul>\r\n<p>&nbsp;che nell\'occasione, fra l\'altro, presenteranno i loro lavori passati, presenti e futuri.<br />Fate un salto, sorseggiate un aperitivo e non ve ne pentirete!</p>\r\n<p>Dopo quattro Notti dei Vecchi Videogiochi (<a href=\"../NVV2014/index.html\">2014</a>,&nbsp;<a href=\"../NVV2014if/index.html\">2014 IF</a>,&nbsp;<a href=\"../NVV2015/index.html\">2015</a>&nbsp;e&nbsp;<a href=\"../NVV2015airo/index.html\">2015 AIRO</a>), edugiochi ad hoc (<a href=\"../Blob/index.html\">Blob Game of Life</a>),<br />un&nbsp;<a href=\"../LabVG/index.html\">corso dedicato</a>&nbsp;e un intervento a&nbsp;<a href=\"../ICEC2015/index.html\">ICEC 2015</a>,<br />quattro chiacchiere in buona compagnia<br />e con un bicchiere in mano ci volevano proprio :)<br />L\'occasione serve anche a riprendere un modo di parlare di informatica e dintorni che HMR aveva sperimentato insieme a&nbsp;<a href=\"../PaginaQ/index.html\">paginaQ</a>&nbsp;e di cui sentiamo la mancanza.</p>\r\n<p>L\'<a href=\"../Riferimenti.html#2016i-ChiacchiereVG\">introduzione</a>&nbsp;alla chiacchierata.</p>', 'HMR_2016d_GCTM-ChiacVG_t.jpg', 'HMR_2016d_GCTM-ChiacVG.jpg', 'http://www.progettohmr.it/ChiacchiereVG/'),
(86, 'no', '', 'HMR & Friends a Vicoretr√≤', '<a href=\'http://www.vicoretro.org/\'>Vicoretr√≤</a>, 5 giugno 2016, dalle 11 alle 19,  Palazzetto dello Sport, p.zza Ferrucci, Vicopisano', '<p>A Vicopisano &egrave; nata Vicoretr&ograve;, un\'associazione culturale dedicata al mantenimento e alla diffusione<br />della vecchia informatica.<br />Per inaugurare le proprie attivit&agrave; Vicoretr&ograve; ha organizzato un\'evento-mostra che, in pochi giorni, ha coinvolto istituzioni e appassionati che da tutta Italia contribuiranno a realizzare 90 m lineari di esposizione: ne vedremo di belle!<br />HMR partecipa con un\'esposizione organizzata insieme ai collezionisti Massimo Concas, Francesco Gori &amp; Daniele Minneci, Alessandro Magnani, Tommaso Mongelli.<br />La selezione di pezzi riprende alcuni temi della storia del calcolo personale gi&agrave; esplorati nell\'allestimenti permanente&nbsp;<a href=\"../Riferimenti.html#2014s-AritPCArt\">Dall\'Aritmometro al PC</a>&nbsp;al Museo degli Strumenti per il Calcolo e nella recente Mostra&nbsp;<a href=\"../ComputersForTheMasses/index.html\">Computers for the masses</a>&nbsp;al Museo Tecnico Navale della Spezia.<br />La scelta dei pezzi si &egrave; concentrata su macchine meno note e su alcuni esempi di portabilit&agrave; e di vetero connettivit&agrave;.<br />In esposizione anche due pezzi collegati ai divertimenti collaterali di HMR: un tuffo musicale nel passato analogico di&nbsp;<a href=\"../Museo.html#PrimaMP3\">prima dell\'emmepitr&egrave;</a>&nbsp;e la console protagonista dell\'imminente prossima edizione della&nbsp;<a href=\"../Museo.html#NVV\">Notte dei Vecchi Videogiochi</a>.<br />Poich&eacute; la Storia non &egrave; solo hardware e software, ci saranno anche due appuntamenti dedicati all\'informatica raccontata:</p>\r\n<p>&nbsp;&nbsp;<strong>15:00</strong>&nbsp;&nbsp;<a href=\"../Riferimenti.html#2016i-Vicoretro\">storie del calcolo personale</a><br /><strong>&nbsp;&nbsp;17:00</strong>&nbsp;&nbsp;<a href=\"../Riferimenti.html#2016i-Vicoretro\">il demo-racconto della Macchina Ridotta</a></p>\r\n<p>Qualche nota in pi&ugrave; sulle&nbsp;<a href=\"../Riferimenti.html#2016d-VicoMiniExpo\">macchine in esposizione</a><br />e sugli&nbsp;<a href=\"../Riferimenti.html#2016d-VicoPrimaMP3\">assaggi di prima dell\'emmepitr&egrave;</a>.</p>', 'HMR_2016d_HMRFriends_t.jpg', '2016_VicoretroLoc.jpg', 'http://www.progettohmr.it/HMRFriends/'),
(89, 'no', '', 'Can You Stand a Chance? La Notte dei Vecchi Videogiochi 2016', 'Mercoled√¨ 29 giugno, Museo degli Strumenti per il Calcolo dalle 15:30 (qualifiche) e dalle 20:30 (torneo)', '<p>Pronti per l\'edizione 2016&nbsp;<br />della Notte dei Vecchi Videogiochi?<br />Sar&agrave; meglio di s&igrave;, perch&eacute; questa volta si picchia duro!<br />Correva l\'anno 1991 quando nelle sale giochi<br />apparve&nbsp;<em>Street Fighter II</em>.<br />Non era il primo gioco di questo tipo, era gi&agrave; un sequel,<br />ma lasci&ograve; il segno nella storia dei videogiochi diventando<br />il titolo pi&ugrave; amato e pi&ugrave; rappresentativo del genere.<br />Per la NVV n. 5 ve lo proponiamo nella versione<br />per Nintendo Super Famicom, la prima che arriv&ograve; nelle case.<br />Quindi sotto con le combo, mostrate il vostro valore<br />e non sottretevi alla sfida!<br />E se proprio temete di farvi del male,<br />venite lo stesso al Museo, visiterete le sale,<br />gusterete l\'aperitivo preparato da&nbsp;<a href=\"http://caffeletterariovoltapagina.blogspot.it/\" target=\"_blank\" rel=\"noopener\">Voltapagina</a>,<br />sarete testimoni di a scontri epici, vi farete trascinare<br />dall\'irresistibile telecronaca di&nbsp;<a href=\"http://www.radiocicletta.it/\" target=\"_blank\" rel=\"noopener\">Radiocicletta.it</a>.<br />La partecipazione &egrave; aperta a tutti e gratuita.<br />Per prendere parte al torneo occorre iscriversi (dal 19/06)<br />tramite una mail a&nbsp;<a href=\"mailto:maukemirras@gmail.com\">maukemirras@gmail.com</a><br />secondo quanto scritto nel&nbsp;<a href=\"HMR_NVV-Reg2016-5.03.pdf\">Regolamento</a>&nbsp;del torneo.<br />Il torneo si svolger&agrave; al Museo degli Strumenti per il Calcolo, via Bonanno Pisano, 2/B, Pisa (<a href=\"../Documenti/HMR_2011d_MappaMuseoSC.pdf\">mappa</a>).</p>\r\n<p>Le altre NVV:&nbsp;<a href=\"../NVV2014/index.html\">2014</a>,&nbsp;<a href=\"../NVV2014if/index.html\">2014 IF</a>,&nbsp;<a href=\"../NVV2015/index.html\">2015</a>,&nbsp;<a href=\"../NVV2015airo/index.html\">2015 AIRO<br /></a></p>', 'HMR_2016d_GCTM-NVV5-Loc_t.jpg', 'HMR_2016d_GCTM-NVV5-Loc.jpg', 'http://www.progettohmr.it/NVV2016/'),
(94, 'no', '', 'L\'aritmometro di \"Marconi\"', '', '', '', '', 'http://www.progettohmr.it/FestivalScienza2016/'),
(101, 'no', '', 'Dall\'aritmometro al calcolatore, personalmente', '4-7 ottobre, Museo San Matteo, Internet Festival 2012', '<p>HMR partecipa all\'<a href=\"http://2012.internetfestival.it/\" target=\"_blank\" rel=\"noopener\">Internet Festival 2012</a>&nbsp;con una mostra<br />su storie, meccanismi e curiosit&agrave; del calcolo personale.<br />Allestita al Museo Nazionale di S. Matteo, la mostra<br />offre una selezione di macchine personali prese dalla ricca&nbsp;<br />collezione del&nbsp;<a href=\"../Museo.html\">Museo degli Strumenti per il Calcolo</a>.</p>\r\n<p>Oltre al fascino singolare di ogni pezzo, l\'insieme spiega<br />l\'evoluzione dagli&nbsp;<strong>aritmometri</strong>&nbsp;dell\'800 ai&nbsp;<strong>PC portatili</strong>,<br />con esponenti significativi per ogni tappa.<br />Un viaggio che tocca diversi pezzi di storia nazionale<br />e molti protagonisti di passioni collettive.</p>\r\n<p>Fra gli aspetti pi&ugrave; tecnologici e caratteristici<br />del \"marchio\" HMR, la Mostra svela il funzionamento<br />delle calcolatrici meccaniche dell\'800.<br />&Egrave; il risultato di un\'accurato lavoro di ricerca svolto sui pezzi<br />del Museo per riscoprire le differenze e le caratteristiche<br />dei vari modelli e di una delicata sperimentazione<br />per verificarne le condizioni e comprenderne i meccanismi.</p>\r\n<p>Un video, mostrando in funzione le macchine della Mostra,<br />spiegher&agrave; i princip&icirc; del funzionamento di questi gioielli<br />della meccanica, di fatto gli stessi delle operazioni in colonna<br />che impariamo alle elementari.<br />E che conosciamo grazie a Leonardo Fibonacci,<br />il matematico pisano che, dopo averli appresi dagli Arabi<br />nei suoi viaggi, li diffuse in Europa nel 1200.</p>\r\n<p>Da questa esperienza, nel 2013, il Museo ha allestito<br />l\'esposizione permanente&nbsp;<a href=\"../Museo.html#AritPC\">Dall\'Aritmometro al PC</a>.</p>', 'HMR-IF2012.jpg', 'HMR-IF2012-HR.jpg', 'http://www.progettohmr.it/IF2012/'),
(102, 'no', '', 'Caratteri/Emozioni dall\'ASCIIart agli Emoticon', '10-13 ottobre, Stazione Leopolda, Internet Festival 2013', '<p>Internet usa ancora molto testo scritto.<br />Ma con qualche libert&agrave;. Tutti conosciamo LOL e :)<br />Un nuovo linguaggio? Non esageriamo.<br />Semmai un accento, un segno grafico che d&agrave; tono,<br />distingue e rende riconoscibile.</p>\r\n<p>Caratteri/Emozioni &egrave; un omaggio alle radici lontane<br />degli emoticon, all\'arte di giocare con i caratteri<br />e alla fantasia di tipografi, scrittori, grafici e informatici.<br />Sperando di soddisfare qualche curiosit&agrave;.<br />E di farne nascere altre.</p>\r\n<p>HMR investiga la storia dell\'informatica con rigore tecnologico senza per&ograve; dimenticare che i limiti e i vantaggi delle macchine condizionano chi le usa e ne stimolano la fantasia.</p>\r\n<p>Caratteri/Emozioni &egrave; un percorso alla scoperta<br />di una giocosa ribellione ai limiti imposti dalle tecnologie,<br />prima dalla tipografia e poi dalle codifiche digitali.</p>', 'HMR_2013d_GC-CarEmo00f_t.jpg', 'HMR_CaratteriEmozioni-lv1t.jpg', 'http://www.progettohmr.it/IF2013/'),
(103, 'no', '', 'Questioni di Carattere', '9 maggio - 2 giugno 2014, Museo della Grafica', '<p>La storia della scrittura a stampa &egrave; una continua ricerca<br />di carattere. Dai primi incisori, alle grandi fonderie,<br />fino al digitale, ogni carattere &egrave; nato per essere diverso,<br />per desiderio del disegnatore o per richiesta<br />di originalit&agrave; di un committente. Non &egrave; facile distinguersi mantenendosi dentro una rigida gabbia di forme e vincoli. I glifi (lettere, cifre, simboli) sono segni convenzionali e devono essere leggibili. Poi ci sono le regole tipografiche e i limiti delle tecnologie. L\'arte dei caratteri &egrave; nei dettagli. La Mostra in sedici tavole originali in tecnica mista,<br />stampa digitale e lettering a mano, riprende il tema appena<br />accennato in&nbsp;<a href=\"../Museo.html#CarEmo-IF\">Caratteri/Emozioni</a>&nbsp;e lo approfondisce<br />in un\'antologia essenziale di caratteri.<br />Ne racconta le storie e guida l\'occhio a scoprirne<br />le differenze per gustare il piacere di saper riconoscere,<br />per dire, una pagina di Sellerio da una di Adelphi.<br />Oppure per trovare il vostro carattere fra i tanti<br />che oggi sono disponibili in digitale.<br />La rassegna adotta la classificazione vox-atypi,<br />esplorando nove delle sue quattordici classi,<br />quelle propriamente tipografiche.<br />La vox-atypi segue l\'evoluzione nel tempo degli stili,<br />ma in una stessa classe possono convivere<br />sia caratteri storici sia disegni recenti.<br />Il percorso della Mostra include tre macchine, scelte<br />dalla collezione del&nbsp;<a href=\"../Museo.html\">Museo degli Strumenti per il Calcolo</a>,<br />testimoni di altrettante tappe della disponibilit&agrave; di caratteri<br />per le nostre stampe personali: uno, alcuni, infiniti.</p>\r\n<p>&nbsp;</p>', 'HMR_2014-QuestioniDiCarattere_t.jpg', 'HMR_2014-QuestioniDiCarattere.jpg', 'http://www.progettohmr.it/QuestioniDiCarattere/'),
(104, 'no', '', 'Prepare to be invaded La Notte dei Vecchi Videogiochi - I ed.', 'Notte dei Musei 2014, Sabato 17 maggio, dalle 14 alle 24 al Museo degli Strumenti per il Calcolo', '<p>Per una Notte dei Musei diversa dal solito, seriamente<br />e informaticamente giocosa, HMR propone un torneo<br />su un videogiocoche ha fatto la storia. Quale?<br />Beh, se non l\'avete capito &egrave; una ragione in pi&ugrave; per venire!<br />Vi promettiamo un campo di gara interessante,<br />ma non gli arcade originali. Forse per le prossime edizioni,<br />se il Museo otterr&agrave; il successo e la visibilit&agrave; che merita.<br />Quindi partecipate numerosi.<br />Fra un fase e l\'altra del torneo ospiti e chiacchiere<br />su calcolatori, giochi e dintorni. Per tutta la serata<br />ogni sala del Museo &egrave; aperta e visitabile.<br />La partecipazione alla Notte &egrave; aperta a tutti e gratuita.<br />Per prendere parte al torneo occorre iscriversi (dal 28 aprile)<br />tramite una mail a&nbsp;<a href=\"mailto:francesca.corradi@unipi.it\">francesca.corradi@unipi.it</a><br />secondo quanto scritto nel&nbsp;<a href=\"HMR_NVV-Reg2014-1.02.pdf\">Regolamento</a>&nbsp;del torneo.<br />Il torneo si svolger&agrave; al Museo degli Strumenti per il Calcolo, via Bonanno Pisano, 2/B, Pisa (<a href=\"../Documenti/HMR_2011d_MappaMuseoSC.pdf\">mappa</a>).<br />Le iscrizioni aprono alle 00.00 del 28 aprile.<br />Le registrazioni aprono alle 14.00 del 17 maggio.<br />Per ogni domanda potete approfittare della cortesia e della pazienza di&nbsp;<a href=\"mailto:francesca.corradi@unipi.it\">Francesca Corradi</a>&nbsp;(050 2213626, lu-ve 9-13).<br />Ma prima leggete il...&nbsp;<a href=\"HMR_NVV-Reg2014-1.02.pdf\">Regolamento</a>&nbsp;:)<br />Le altre NVV:&nbsp;<a href=\"../NVV2014if/index.html\">NVV2014 IF</a>,&nbsp;<a href=\"../NVV2015/index.html\">NVV2015</a>,&nbsp;<a href=\"../NVV2015airo/index.html\">NVV2015 AIRO</a></p>', 'HMR_NVV2014_t.jpg', 'HMR_NVV2014.jpg', 'http://www.progettohmr.it/NVV2014/'),
(105, 'no', '', 'Prima dell\'emmepitr√©', 'Venerd√¨ 20 giugno 2014, dalle 20.30, serata musicale a tema al Museo degli Strumenti per il Calcolo', '<p>Suono e musica sono dati: si registrano, si conservano,<br />si riproducono. Dal fonografo del 1877 ai formati digitali<br />di oggi, le tecnologie hanno segnato l\'ascolto della musica contribuendo a farla diventare un fenomeno diffuso. Le tecnologie influenzano anche come la musica si produce, mentre le suggestioni del progresso ispirano i musicisti. Infine, la musica fa da sottofondo al tempo che passa.<br />HMR propone una serata al Museo dedicata a un periodo interessante tanto per l\'informatica italiana (e pisana), quanto per la produzione musicale.<br />Da met&agrave; degli anni \'50 agli anni \'70 Pisa ha visto il concepimento del progetto CEP all\'Universit&agrave;, la realizzazione della Macchina Ridotta, il primo calcolatore italiano, l\'inizio a Barbaricina dell\'avventura Olivetti, il completamento della seconda CEP, l\'arrivo del 7090 IBM e la creazione del CNUCE, i primi Istituti del CNR, infine, a coronamento di un percorso che unisce la ricerca alla didattica, il Corso di Laurea in Scienze dell\'Informazione, di nuovo un primato nazionale.<br />I brani, scelti dalla collezione del&nbsp;<a href=\"http://www.gaprecordstore.it/\" target=\"_blank\" rel=\"noopener\">Gap Record Store</a>&nbsp;e suonati sulla strumentazione d\'epoca del&nbsp;<a href=\"http://www.nuvistorclub.it/\" target=\"_blank\" rel=\"noopener\">Nuvistor Club</a>, ci faranno rivivere la colonna sonora di quel periodo straordinario: l\'elettronica, protagonista del mondo dei calcolatori, muoveva i primi passi anche come strumento musicale, con Pisa ancora protagonista con le esperienze del Maestro Grossi al CNUCE.</p>', 'MSC_20140620PrimaMP3_t.jpg', 'MSC_20140620PrimaMP3.jpg', 'http://www.progettohmr.it/PrimaMP3/'),
(106, 'no', '', 'Since \'81. And still jumping! La Notte dei Vecchi Videogiochi 2014 IF', '<a href=\'http://2014.internetfestival.it/eventi/la-notte-dei-vecchi-videogiochi/\' target=\'_blank\'>Speciale Internet Festival</a>, 10-11 ottobre, al Museo degli Strumenti per il Calcolo', '<p>Torna la Storia dell\'Informatica seriamente giocosa.<br />Il gioco su cui vi sfidiamo? Lo dovete indovinare,<br />e se non ne avete idea &egrave; un motivo in pi&ugrave; per venire!<br />&Egrave; un\'occasione per provare di persona l\'<a href=\"../PaginaQ/index.html#4CCsC_018\">effetto</a>&nbsp;che fanno<br />i vecchi videogiochi e per visitare il Museo. In pi&ugrave; chiacchiere<br />con il Dott. Monge, approfondimenti, curiosit&agrave; e nostalgie<br />a 8 bit in streaming per tutta la sera.<br />&Egrave; anche un\'anteprima della nuova Sala Laboratori del Museo<br />realizzata da HMR grazie al contributo della Fondazione Pisa<br />e alla collaborazione di molti partner del Museo, fra i quali,<br />in particolare, la Sezione di Pisa dell\'INFN.<br />La partecipazione &egrave; aperta a tutti e gratuita.<br />Per prendere parte al torneo occorre iscriversi (dal 29.09)<br />tramite una mail a&nbsp;<a href=\"mailto:francesca.corradi@unipi.it\">francesca.corradi@unipi.it</a><br />secondo quanto scritto nel&nbsp;<a href=\"HMR_NVV-Reg2014-2.04.pdf\">Regolamento</a>&nbsp;del torneo.<br />Il torneo si svolger&agrave; al Museo degli Strumenti per il Calcolo, via Bonanno Pisano, 2/B, Pisa (<a href=\"../Documenti/HMR_2011d_MappaMuseoSC.pdf\">mappa</a>).<br />Iscrizioni: dalle 00.00 del 29.09.<br />Registrazioni: dalle 15.00 del 10.10, giorno delle qualifiche.<br />Fasi finali del torneo: dalle 20.00 dell\'11.10.<br />Per ogni domanda potete approfittare della cortesia e della pazienza di&nbsp;<a href=\"mailto:francesca.corradi@unipi.it\">Francesca Corradi</a>&nbsp;(050 2213626, lu-ve 9-13).<br />Ma prima leggete il...&nbsp;<a href=\"HMR_NVV-Reg2014-2.04.pdf\">Regolamento</a>&nbsp;:)</p>\r\n<p>Le altre NVV:&nbsp;<a href=\"../NVV2014/index.html\">NVV2014</a>,&nbsp;<a href=\"../NVV2015/index.html\">NVV2015</a>,&nbsp;<a href=\"../NVV2015airo/index.html\">NVV2015 AIRO<br /></a></p>', 'HMR_NVV2014if_t.png', 'HMR_NVV2014if.jpg', 'http://www.progettohmr.it/NVV2014if/'),
(107, 'no', '', 'Sala Laboratori HMR', 'Inaugurazione della sala dedicata alle attivit√† educative e sperimentali del Museo  venerd√¨ 24 ottobre 2014, h 16.00, Museo degli Strumenti per il Calcolo', '<p>Al Museo &egrave; conservata una collezione di strumenti informatici unica in Italia e invidiabile in Europa.<br />Alla ricchezza del patrimonio storico, corrisponde<br />un\'altrettanto ricca offerta di attivit&agrave; educative<br />basate su approfondimenti frutto della ricerca di HMR<br />o su ricostruzioni hardware o software realizzate da HMR.<br />Con la nuova Sala Laboratori, il Museo si dota di un\'area attrezzata per ospitare le ricostruzioni dei calcolatori del passato e per svolgere attivit&agrave; didattiche e sperimentali. L\'inaugurazione &egrave; un\'occasione per presentare agli insegnanti l\'offerta educativa del Museo, tanto le proposte rinnovate di incontri, visite e laboratori da anni parte dell\'offerta esclusiva del Museo, quanto i progetti frutto di nuove collaborazioni.<br />L\'allestimento della Sala Laboratori &egrave; stato possibile<br />grazie al contributo della Fondazione Pisa,<br />della Fondazione Galileo Galilei e<br />alla collaborazione della Sezione di Pisa dell\'INFN.</p>\r\n<p><strong>Programma</strong></p>\r\n<p>16.00 Inaugurazione e saluti istituzionali</p>\r\n<p>17.00 Presentazione delle attivit&agrave; della Sala Laboratori<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<a href=\"../Riferimenti.html#2014i-SalaLab\">Uno spazio attrezzato al MSC</a>, Giovanni A. Cignoni<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<a href=\"HMR_SalaLab-FabLab.pdf\">Attivit&agrave; sulla stampa 3d</a>, Carmelo De Maria<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<a href=\"../Riferimenti.html#2014i-LabVG\">Laboratorio di Videogiochi</a>, T. Mongelli, L. Cappellini</p>\r\n<p>18.30 Brindisi</p>', 'HMR_20141024-SalaLabLoc_t.jpg', 'HMR_20141024-SalaLabLoc.jpg', 'http://www.progettohmr.it/SalaLab/'),
(108, 'no', '', 'Musica e numeri, intermezzi meccanici senza filo', 'Prima dell\'emmepitr√® a <a href=\'http://www.senzafilomusiccontest.eu/\'>Senza Filo</a> dal 6 al 29 novembre al <a href=\'http://www.cantieresanbernardo.com/\'>Cantiere Sanbernardo</a>', '<p>Musica e numeri, senza filo.<br />Ovvero grammofoni e calcolatrici, macchine squisitamente<br />meccaniche dedicate a trattare due tipi di informazioni,<br />musica e numeri, prima di elettricit&agrave; ed elettronica.<br /><a href=\"../Museo.html#PrimaMP3\">Prima dell\'emmepitr&eacute;</a>&nbsp;torna con le sue contaminazioni<br />fra musica e informatica, per partecipare al festival<br />veramente acustico organizzato dal Cantiere Sanbernardo.<br />L\'et&agrave; d\'oro di grammofoni e calcolatrici meccaniche<br />&egrave; raccontata in cinque intermezzi alle serate di Senza Filo,<br />ognuno dedicato a un diverso periodo storico<br />fra l\'inizio del \'900 e i primi anni \'50.<br />Ascolteremo dischi d\'epoca suonati su grammofoni<br />e vedremo le magie del calcolo meccanico sulle calcolatrici<br />della collezione del Museo.</p>\r\n<p><strong>I 5+1 appuntamenti</strong></p>\r\n<ul>\r\n<li>06 novembre -&nbsp;<a href=\"../Riferimenti.html#2014d-SFilo1\">L\'alba del XX secolo</a>&nbsp;(e su&nbsp;<a href=\"../PaginaQ/index.html#4CCsC_023\">PaginaQ</a>)</li>\r\n<li>13 novembre -&nbsp;<a href=\"../Riferimenti.html#2014d-SFilo2\">Gli anni ruggenti</a>&nbsp;(e su&nbsp;<a href=\"../PaginaQ/index.html#4CCsC_024\">PaginaQ</a>)</li>\r\n<li>20 novembre -&nbsp;<a href=\"../Riferimenti.html#2014d-SFilo3\">Momenti neri</a>&nbsp;(e su&nbsp;<a href=\"../PaginaQ/index.html#4CCsC_025\">PaginaQ</a>)</li>\r\n<li>27 novembre -&nbsp;<a href=\"../Riferimenti.html#2014d-SFilo4\">Ma il morale &egrave; alto...</a>&nbsp;(e su&nbsp;<a href=\"../PaginaQ/index.html#4CCsC_026\">PaginaQ</a>)</li>\r\n<li>29 novembre -&nbsp;<a href=\"../Riferimenti.html#2014d-SFilo5\">Ultimi giri</a>&nbsp;(e su&nbsp;<a href=\"../PaginaQ/index.html#4CCsC_027\">PaginaQ</a>)</li>\r\n</ul>\r\n<p>&nbsp;E inoltre l\'<strong>appuntamento speciale</strong>&nbsp;prima del gran finale:</p>\r\n<ul>\r\n<li>sabato 29 novembre, dalle 17:&nbsp;<strong>Senza Filo al Museo!</strong></li>\r\n</ul>\r\n<p>&nbsp;Gli altri eventi Prima dell\'emmepitr&egrave;:<br /><a href=\"../PrimaMP3/index.html\">giugno 2014</a>,&nbsp;<a href=\"../NVV2015/index.html#64PrimaMP3\">giugno 2015</a>,&nbsp;<a href=\"../Documenti/HMR_2015d_GCAM-SFilo.jpg\">dicembre 2015</a>.</p>', 'SenzaFilo2014Loc_t.jpg', 'SenzaFilo2014Loc.jpg', 'http://www.progettohmr.it/SenzaFilo/'),
(109, 'no', '', 'Un mese con Turing e l‚ÄôEnigma', 'dal 28 febbraio al 29 marzo 2015, al Museo degli Strumenti per il Calcolo (e non solo)', '<p>Cogliendo al volo l\'interesse seguito a&nbsp;<strong>The Imitation Game</strong><br />e al suo meritato Oscar (miglior sceneggiatura non originale)<br />HMR &egrave; felice di proporre un ricco programma di eventi:<br />l\'<strong>Enigma</strong>&nbsp;al nostro Museo, e poi approfondimenti su&nbsp;<strong>Turing</strong>&nbsp;e&nbsp;<strong>Bletchley Park</strong>, sulla&nbsp;<strong>crittografia</strong>, sul serio e nella fantasia, nella storia e nel quotidiano, film con presentazioni e dibattiti, il Turing di Tuono Pettinato, tavole rotonde, articoli, dirette streaming e laboratori per le scuole.<br /><strong>Orario della Mostra dal 14 al 22 marzo</strong><br />sabato 14 (inaugurazione), 15.00-19.00<br />domenica 15, sabato 21 e domenica 22, 10.00-19.00<br />da luned&igrave; 16 a venerd&igrave; 20, 9.00-19.00<br /><a href=\"../Documenti/HMR_2011d_MappaMuseoSC.pdf\">Come raggiungere il Museo<br /></a><strong>Info e prenotazioni per le scuole:</strong><br />Francesca Corradi (<a href=\"mailto:francesca.corradi@unipi.it\">francesca.corradi@unipi.it</a>)<br />tel. 050 2213626, luned&igrave;-venerd&igrave;, 9-13<br /><strong>Ultim\'ora, aggiornamenti e dirette su</strong>&nbsp;<a href=\"http://www.facebook.com/MuseoStrumentiCalcolo?ref=hl\" target=\"_blank\" rel=\"noopener\"><img src=\"../images/FacebookButt.png\" alt=\"Facebook\" width=\"20\" height=\"20\" align=\"top\" /></a>&nbsp;e&nbsp;<a href=\"http://twitter.com/PecoriniArianna\" target=\"_blank\" rel=\"noopener\"><img src=\"../images/TwitterButt.png\" alt=\"Twitter\" width=\"20\" height=\"20\" align=\"top\" /><br /></a>Iniziativa realizzata in collaborazione con:</p>\r\n<ul>\r\n<li>i corsi di laurea in&nbsp;<a href=\"http://ce.iet.unipi.it/\" target=\"_blank\" rel=\"noopener\">Ingegneria Informatica</a>&nbsp;e&nbsp;<a href=\"http://www.di.unipi.it/didattica\" target=\"_blank\" rel=\"noopener\">Informatica</a></li>\r\n<li>il&nbsp;<a href=\"http://www.sma.unipi.it/\" target=\"_blank\" rel=\"noopener\">Sistema Museale d\'Ateneo</a></li>\r\n<li>il&nbsp;<a href=\"http://cultura.mise.gov.it/museoPPTT_fe\" target=\"_blank\" rel=\"noopener\">Museo Storico della Comunicazione</a>&nbsp;del&nbsp;<a href=\"http://www.sviluppoeconomico.gov.it/\" target=\"_blank\" rel=\"noopener\">MiSE</a></li>\r\n<li>la&nbsp;<a href=\"http://www.fondazionegalileogalilei.it/\" target=\"_blank\" rel=\"noopener\">Fondazione Galileo Galilei</a></li>\r\n<li>il&nbsp;<a href=\"http://www.arsenalecinema.it/\" target=\"_blank\" rel=\"noopener\">Cinema Arsenale</a></li>\r\n<li>il quotidiano online&nbsp;<a href=\"http://www.paginaq.it/\" target=\"_blank\" rel=\"noopener\">paginaQ</a></li>\r\n<li>la webradio&nbsp;<a href=\"http://www.radiocicletta.it/\" target=\"_blank\" rel=\"noopener\">Radiocicletta.it</a></li>\r\n<li>il&nbsp;<a href=\"http://www.teatrorossiaperto.it/\" target=\"_blank\" rel=\"noopener\">Teatro Rossi Aperto</a></li>\r\n</ul>', 'HMR_2015-TuringEnigma_t.jpg', 'HMR_2015-TuringEnigma.jpg', 'http://www.progettohmr.it/TuringEnigma/'),
(110, 'no', '', 'Da Vito Volterra alla simulazione ad agenti', 'Sabato 16 maggio 2015, dalle 20.00, La Notte dei Musei al Museo degli Strumenti per il Calcolo', '<p>Il tema assegnato alla Notte dei Musei quest\'anno &egrave; la&nbsp;<strong>sostenibilit&agrave;</strong>, ai Musei va il compito di declinare l\'argomento<br />in termini della storia che conservano e raccontano.<br />HMR ha raccolto l\'invito e vi propone una serata<br />al Museo degli Strumenti per il Calcolo dedicata a quei<br />delicati equilibri da cui dipende la sostenibilit&agrave; della Terra.<br />Ovviamente visti con la lente del calcolo e dell\'informatica.<br /><strong>Vito Volterra</strong>&nbsp;dette molti contributi alla matematica e all\'organizzazione della ricerca in Italia.<br />Fra i suoi risultati pi&ugrave; noti c\'&egrave; il&nbsp;<strong>modello prede-predatori</strong>:<br />un sistema di equazioni che ben descrive gli equilibri<br />di molti ecosistemi naturali.<br />Lo svilupp&ograve; negli Anni \'20 del secolo scorso.<br />La&nbsp;<strong>simulazione ad agenti</strong>&nbsp;&egrave; uno strumento dell\'informatica moderna che ci permette di ricreare ecosistemi virtuali, modellando il comportamento dei singoli individui<br />e osservando cosa succede. Come in un gioco, ma serissimo.<br />E proprio un&nbsp;<strong><a href=\"../Blob/index.html\">videogioco</a></strong>&nbsp;abbiamo inventato per l\'occasione.<br />Vi sfidiamo a costruire e mantenere l\'equilibrio<br />fra due popolazioni di organismi unicellulari. Ci riuscirete?<br />In ogni caso sperimenterete quanto delicati e complessi<br />siano gli equilibri che governano la sostenibilit&agrave; del Pianeta.<br />Le altre Notti dei Musei firmate HMR:&nbsp;<a href=\"../NotteMusei2013/index.html\">2013</a>,&nbsp;<a href=\"../NVV2014/index.html\">2014</a></p>', 'HMR_NotteMusei2015Loc_t.jpg', 'HMR_NotteMusei2015Loc.jpg', 'http://www.progettohmr.it/NotteMusei2015/'),
(111, 'no', '', 'Don\'t be shy La Notte dei Vecchi Videogiochi 2015', 'Sabato 30 maggio, dalle 14 alle 24 al Museo degli Strumenti per il Calcolo', '<p>Non c\'&egrave; due senza tre: torna la Storia dell\'Informatica<br />per chi&nbsp;<a href=\"../PaginaQ/index.html#4CCsC_021\">gioca sul serio</a>.<br />La macchina e il gioco su cui vi sfidiamo?<br />Come al solito li dovete indovinare (ma &egrave; facile)<br />e se non ne avete idea &egrave; un motivo in pi&ugrave; per venire!<br />Qualche indizio? Non &egrave; pi&ugrave; il&nbsp;<a href=\"../PaginaQ/index.html#4CCsC_018\">NES</a>, ma come processore<br />non siamo molto lontani.<br />Col la NVV si inaugura anche la Mostra&nbsp;<strong>64 Mania</strong>,<br />un ulteriore motivo per visitare il Museo.<br />In pi&ugrave; interventi e chiacchiere con il Dott. Monge e con<br />chi ci sar&agrave;, approfondimenti, curiosit&agrave; e nostalgie a 8 bit.<br />La partecipazione &egrave; aperta a tutti e gratuita.<br />Per prendere parte al torneo occorre iscriversi (dal 18.05)<br />tramite una mail a&nbsp;<a href=\"mailto:francesca.corradi@unipi.it\">francesca.corradi@unipi.it</a><br />secondo quanto scritto nel&nbsp;<a href=\"HMR_NVV-Reg2015-3.02.pdf\">Regolamento</a>&nbsp;del torneo.<br />Il torneo si svolger&agrave; al Museo degli Strumenti per il Calcolo, via Bonanno Pisano, 2/B, Pisa (<a href=\"../Documenti/HMR_2011d_MappaMuseoSC.pdf\">mappa</a>).<br />Iscrizioni: dalle 00.00 del 18.05.<br />Registrazioni: dalle 14.00 del 30.05, le qualifiche a seguire.<br />Fasi finali del torneo: dalle 20.00 del 30.05.<br />Per ogni domanda potete approfittare della cortesia e della pazienza di&nbsp;<a href=\"mailto:francesca.corradi@unipi.it\">Francesca Corradi</a>&nbsp;(050 2213626, lu-ve 9-13).<br />Ma prima leggete il...&nbsp;<a href=\"HMR_NVV-Reg2015-3.02.pdf\">Regolamento</a>&nbsp;:)</p>\r\n<p>Le altre NVV:&nbsp;<a href=\"../NVV2014/index.html\">NVV2014</a>,&nbsp;<a href=\"../NVV2014if/index.html\">NVV2014 IF</a>,&nbsp;<a href=\"../NVV2015airo/index.html\">NVV2015 AIRO</a></p>', 'HMR_NVV2015-Loc_t.jpg', 'HMR_NVV2015-Loc.jpg', 'http://www.progettohmr.it/NVV2015/'),
(113, 'no', '', 'Killing polyhedral models La Notte dei Vecchi Videogiochi 2015', '<a href=\'http://www.airo.org/conferences/airo2015/e-sport\'>speciale AIRO 1st e-sport competition</a>, 8 settembre, al Museo degli Strumenti per il Calcolo via Bonanno Pisano, 2/B, Pisa (mappa)', '<p>Torna la NVV in un\'edizione speciale per la Conferenza<br />dell\'Associazione Italiana di Ricerca Operativa.<br />Speciale anche per la formula del torneo:<br />la prima NVV su un FPS con confronti diretti a squadre!<br />Il gioco? Non &egrave; il solito titolone, ma la NVV &egrave; fatta anche<br />per&nbsp;<a href=\"../Documenti/HMR_2015d_GCAF-NVVairoInfo.pdf\">raccontare cose</a>&nbsp;e portare alla vostra ludica attenzione<br />giochi poco noti. Ma meritevoli.<br />Per esempio un figlio del primo FPS pensato specificatamente multiplayer, nato come mod e distribuito open source.<br />Fra gli scopi di AIRO c\'&egrave; anche far conoscere al pubblico<br />la ricerca operativa, una disciplina assai legata all\'informatica<br />e con... poliedriche ricadute applicative.<br />Quindi, per participare alla NVV dovrete mostrare un sincero<br />interesse impegnandovi, come recita il&nbsp;<a href=\"../Documenti/HMR_2015d_GCAF-NVVairoReg-1.10it.pdf\">Regolamento</a>,<br />a \"motivare, ancorch&eacute; in modo arbitrariamente fantasioso,<br />di avere una qualsivoglia relazione con la conferenza\" :)<br />L\'iscrizione &egrave; gratuita, scrivere a&nbsp;<a href=\"mailto:frangio@di.unipi.it\">frangio@di.unipi.it</a><br />secondo quanto scritto nel&nbsp;<a href=\"../Documenti/HMR_2015d_GCAF-NVVairoReg-1.10it.pdf\">Regolamento</a>&nbsp;del torneo.<br />Il Museo sar&agrave; aperto e visitabile tutta la serata.<br />Per ogni domanda:&nbsp;<a href=\"mailto:frangio@di.unipi.it\">frangio@di.unipi.it</a>,<br />ma prima leggete il...&nbsp;<a href=\"../Documenti/HMR_2015d_GCAF-NVVairoReg-1.10it.pdf\">Regolamento</a>!</p>\r\n<p>Le altre NVV:&nbsp;<a href=\"../NVV2014/index.html\">NVV2014</a>,&nbsp;<a href=\"../NVV2014if/index.html\">NVV2014 IF</a>,&nbsp;<a href=\"../NVV2015/index.html\">NVV2015</a></p>', 'HMR_NVV2015airo_t.jpg', 'HMR_NVV2015airo.jpg', ' http://www.progettohmr.it/NVV2015airo/'),
(114, 'no', '', 'Computers for the Masses', 'Dal 25 settembre 2015 al 28 febbraio 2016, una mostra del <a href=\'http://www.marina.difesa.it/storiacultura/ufficiostorico/musei/museotecnav\'>Museo Tecnico Navale della Spezia</a>', '<p>Uno smartphone, un tablet, una console per videogiochi<br />sono strumenti di calcolo. Anche un PC, ovviamente.<br />Ci sono calcoli per tutti i gusti.<br />Da quelli proprio facili come una divisione con il divisore<br />a sette cifre, a quelli veramente complessi come le schermate<br />disegnate ogni frazione di secondo per farci vivere<br />nel mondo virtuale di un videogioco.<br />Calcolatrici e calcolatori sono le macchine che ci aiutano<br />a eseguire questi calcoli. La mostra parla di loro.<br />La loro storia inizia molto tempo fa.<br />Sono tutte macchine digitali, meccaniche e decimali prima,<br />elettroniche e binarie poi.<br />Non &egrave; tutta la storia: richiederebbe molto pi&ugrave; spazio.<br />Sono istantanee. Scelte cercando di cogliere anche<br />qualcuno dei momenti meno noti.<br />Per raccontarla abbiamo riunito diversi attori impegnati<br />nel conservare e diffondere la storia dell\'informatica.<br />Purtroppo non tutti: richiederebbe molto pi&ugrave; spazio.<br />&Egrave; un inizio.<br />Computers for the masses &egrave; un motto preso a prestito<br />da Jack Tramiel, uno dei tanti protagonisti del periodo che,<br /> fra fine anni \'70 e inizio anni \'90, ha visto l\'informatica<br /> trasformarsi da oggetto di ricerca a strumento di lavoro,<br /> poi hobby, infine origine di accessori<br />di uso naturale e quotidiano.<br /><br />Inaugurazione 25 settembre, per la Notte dei Ricercatori</p>', 'HMR_2015d_GC-CompMass-Loc_t.jpg', 'HMR_2015d_GC-CompMass-Loc.jpg', 'http://www.progettohmr.it/ComputersForTheMasses/'),
