INSERT INTO `happenings` (`IdHa`, `Date`, `Title`, `Subtitle`, `Anchor`, `State`, `IdCr`, `IdEv`, `IdBi`, `IdHl`, `IdPg`) VALUES
(1, '2008-03-07', 'Prima della CEP: la Macchina Ridotta, 50 anni fa a Pisa', 'Lezione al corso di Storia dell\'Informatica 2007/08', '2008i-LezSti', 'Pubblicato', 1, 1, 1, 0, 0),
